public class Edureka_Array {
    public static void main(String[] args) {
        //declare an array
        //arrays allow you to declare sequential data and one data type-ca
        int n = 10; //array length
        int a[][] = {{1, 2, 3}, {4, 5, 6}, {7, 8, 9}};//3 X 3 multidimensional array
        int b[] = {44,55,66,99};
        int i, j;
//        int[][]b = new int[3][3];                                                                                                           w
        for (i = 0; i < 3; i++) {
            for (j = 0; j < 3; j++) {
                System.out.print(a[i][j] + "  "); //use print and not println if you want your data to matrix up
            }
            System.out.println(); //allows the output to be a matrix format


        }
        for (i=0;i < 4; i++){
            System.out.print(b[i]+" "); //single array..presented as a column using println/row using print
        }

    }
}
